package Arrays;

public class FirstAndLastValuleToPrint {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  
		
		int a[]= {0,1,0,1,85,90};
		
		System.out.println(a[0]);           // print first value of array
	    System.out.println(a[a.length-1]);   // print last value of the array
		
	}

}
